if.if:
AND sb.com_id = (
	SELECT com_id
	FROM COM_MEMBER
	WHERE MEM_SEQ = #{sessionMemSeq}
)

mapper.select:
SELECT CONCAT(RPAD(#{sgrCd}, 2, '0'), LPAD(IFNULL(MAX(CAST(REGEXP_REPLACE(subj_cd, '[^0-9]', '') AS UNSIGNED)), 0) + 1, 4,'0')) AS subj_cd
FROM TB_SUBJ
WHERE SGR_CD = #{sgrCd}

mapper.update:
		UPDATE TB_SUBJ
		SET  cate_cd = #{cateCd}
            ,subj_nm = #{subjNm}
            ,com_id = #{comId}
            ,use_yn = #{useYn}
            ,dupl_enroll_yn = #{duplEnrollYn}
            ,tel = #{tel}
            ,loc_id = #{locId}
            ,subj_plan = #{subjPlan}
            ,subj_desc = #{subjDesc}
            ,enroll_type = #{enrollType}
            ,edu_target = #{eduTarget}
            ,upd_id = #{sessionMemSeq}
            ,upd_ip = #{sessionIp}
            ,upd_dt = NOW()
		WHERE SUBJ_CD = #{subjCd}

mapper.select:
SELECT COUNT(ss.seq_cd)
FROM TB_SUBJ_SEQ ss
WHERE ss.subj_cd = #{subjCd}
