mapper.select:
SELECT COUNT(*) FROM TB_SUBJ_SEQ SS INNER JOIN TB_SUBJ S ON SS.SGR_CD = S.SGR_CD AND SS.SUBJ_CD = S.SUBJ_CD WHERE 1 = 1 AND SS.USE_YN = 'Y'
AND SS.SGR_CD = #{searchSgrCd}
AND SS.CATE_CD = #{searchCateCd}

SELECT SS.SUBJ_NM , SS.SEQ_CD , SS.CATE_CD , SS.SGR_CD ,( SELECT AF.SAVE_FILE_NAME FROM ATCH_FILE AF WHERE AF.THUMBNAIL_CROP = 'Y' AND AF.DELETE_YN = 'N' AND SS.SEQ_CD = AF.P_ID LIMIT 1 ) AS THUMBPATH , DATE_FORMAT(SS.ENROLL_START_DT, '%y.%m.%d') AS ENROLL_START_DT , DATE_FORMAT(SS.ENROLL_END_DT, '%y.%m.%d') AS ENROLL_END_DT , DATE_FORMAT(SS.LEARN_START_DT, '%y.%m.%d') AS LEARN_START_DT , DATE_FORMAT(SS.LEARN_END_DT, '%y.%m.%d') AS LEARN_END_DT
, CASE WHEN NOW() < SS.ENROLL_START_DT THEN 'be' WHEN NOW() BETWEEN SS.ENROLL_START_DT AND SS.ENROLL_END_DT AND (SS.CAPACITY > (SELECT COUNT(*) FROM TB_ENROLL E WHERE E.SEQ_CD = SS.SEQ_CD AND E.ENROLL_STATUS_CD = 'B') OR (SS.CAPACITY <= (SELECT COUNT(*) FROM TB_ENROLL E WHERE E.SEQ_CD = SS.SEQ_CD AND E.ENROLL_STATUS_CD = 'B') AND SS.WAIT_ENROLL_CNT > (SELECT COUNT(*) FROM TB_ENROLL E WHERE E.SEQ_CD = SS.SEQ_CD AND E.ENROLL_STATUS_CD = 'A')) ) THEN 'ing' ELSE 'end' END AS STATUS
, SS.WAIT_ENROLL_CNT , SS.CAPACITY , SS.EDU_TARGET FROM TB_SUBJ_SEQ SS INNER JOIN TB_SUBJ S ON SS.SGR_CD = S.SGR_CD AND SS.SUBJ_CD = S.SUBJ_CD WHERE 1 = 1 AND SS.USE_YN = 'Y'
AND SS.SGR_CD = #{searchSgrCd}
AND SS.CATE_CD = #{searchCateCd}
ORDER BY SS.ENROLL_START_DT DESC, SS.ENROLL_END_DT DESC
